mapper.select:
SELECT B_SEQ, ADMIN_ID, B_TITLE, B_CONTENT, B_READ_CNT, REG_DATE FROM (SELECT ROWNUM AS RNUM, B_SEQ, ADMIN_ID, B_TITLE, B_CONTENT, B_READ_CNT, REG_DATE FROM (SELECT B_SEQ, NVL(ADMIN_ID, '') ADMIN_ID, NVL(B_TITLE, '') B_TITLE, NVL(B_CONTENT, '') B_CONTENT, NVL(B_READ_CNT, 0) B_READ_CNT, NVL(TO_CHAR(REG_DATE, 'YYYY.MM.DD HH24:MI:SS'), '') REG_DATE FROM WD_NBOARD WHERE 1 = 1
AND B_TITLE LIKE '%' || #{searchValue} || '%'
ORDER BY B_SEQ DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE WD_NBOARD
   SET B_READ_CNT = B_READ_CNT + 1
 WHERE B_SEQ = #{value}
